import React, { Component } from "react";

const startFormConfig = {
  name: "",
  nameError: "",
  email: "",
  emailError: "",
  password: "",
  passwordError: "",
  url: "",
  urlError: ""
};

class Form extends Component {
  state = startFormConfig;

  submited = event => {
    event.preventDefault();
    let res = this.validate();

    if (res) {
      console.log("nema greske");
    } else {
      console.log("ima greske");
    }
    console.log(this.state.nameError, this.state.emailError);
  };

  validate = () => {
    var nameError = "";
    var emailError = "";
    var urlError = "";

    if (this.state.name.length < 3 || this.state.name.length > 30) {
      nameError = "Your name have to have between 3 and 30 characters";
      console.log("STIGOJE");
    }
    if (!this.state.email.includes("@") && !this.state.email.includes(".")) {
      emailError = "Email must contain @ and .";
      console.log("SLETOJEJEJEJE");
    }
    if (nameError || emailError) {
      this.setState({ nameError, emailError });
      return false;
    }

    return true;
  };

  changed = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    return (
      <div>
        <h1>Forma ideale</h1>
        <form onSubmit={this.submited}>
          <div>
            <label>name</label>
            <input
              name="name"
              type="text"
              placeholder="Your name"
              value={this.state.name}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>e-mail</label>
            <input
              type="text"
              name="email"
              placeholder="Your email"
              value={this.state.email}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>password</label>
            <input
              type="text"
              name="password"
              placeholder="Your password"
              value={this.state.password}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>URL</label>
            <input
              type="text"
              name="url"
              placeholder="https://example.com"
              value={this.state.url}
              onChange={this.changed}
            />
          </div>
          <button type="submit">Submit here !</button>
        </form>
        <p>{this.state.name}</p>
        <p>{this.state.email}</p>
        <p>{this.state.password}</p>
        <p>{this.state.url}</p>
      </div>
    );
  }
}

export default Form;
