import React, { Component } from "react";

const startFormConfig = {
  name: "",
  nameError: "",
  email: "",
  emailError: "asas",
  password: "asas",
  passwordError: "",
  url: "",
  urlError: ""
};

class Form extends Component {
  state = startFormConfig;

  render() {
    return (
      <div>
        <h1>Forma ideale</h1>
        <div>
          <label>name</label>
          <input
            name="name"
            type="text"
            value={this.state.name}
            onChange={e => {
              this.setState({ name: e.target.value });
            }}
          />
        </div>
        <div>
          <label>e-mail</label>
          <input type="email" />
        </div>
        <div>
          <label>password</label>
          <input type="password" />
        </div>
        <div>
          <label>URL</label>
          <input type="url" />
        </div>
        <p>{this.state.name}</p>
        <p>{this.state.email}</p>
        <p>{this.state.password}</p>
        <p>{this.state.url}</p>
      </div>
    );
  }
}

export default Form;
