import React, { Component } from "react";

const startFormConfig = {
  name: "",
  nameError: "",
  email: "",
  emailError: "",
  password: "",
  passwordError: "",
  url: "",
  urlError: ""
};

class Form extends Component {
  state = startFormConfig;

  changed = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    return (
      <div>
        <h1>Forma ideale</h1>
        <form>
          <div>
            <label>name</label>
            <input
              name="name"
              type="text"
              value={this.state.name}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>e-mail</label>
            <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>password</label>
            <input
              type="password"
              name="password"
              value={this.state.password}
              onChange={this.changed}
            />
          </div>
          <div>
            <label>URL</label>
            <input
              type="url"
              name="url"
              value={this.state.url}
              onChange={this.changed}
            />
          </div>
        </form>
        {this.state.value}
      </div>
    );
  }
}

export default Form;
