import React, { Component } from "react";

const startFormConfig = {
  name: "",
  nameError: "",
  email: "",
  emailError: "",
  password: "",
  passwordError: "",
  url: "",
  urlError: ""
};

class Form extends Component {
  state = startFormConfig;

  capitalize = name => {
    this.setState({
      name: name.charAt(0).toUpperCase() + name.slice(1).toLowerCase()
    });
  };

  submited = event => {
    event.preventDefault();
    let res = this.validate();

    if (res) {
      console.log("nema greske");
    } else {
      console.log("ima greske");
    }
    console.log(this.state.nameError.length, this.state.emailError.length);
  };

  validate = () => {
    let nameError = "";
    let emailError = "";
    let urlError = "";
    let emailFormat = new RegExp(/^[a-zA-Z0-9]+@[a-zA-Z0-9]+.[A-Za-z]+$/);

    if (this.state.name.length < 3 || this.state.name.length > 30) {
      nameError = "Your name have to have between 3 and 30 characters";
    }
    if (!emailFormat.test(this.state.email)) {
      emailError = "Email is not in a valid format!";
    }
    if (nameError || emailError) {
      this.setState({ nameError, emailError });
      return false;
    } else {
      this.setState({ nameError, emailError });
    }
    return true;
  };

  changed = event => {
    if (event.target.name === "name") {
      event.target.value = this.capitalize(event.target.value);
    }

    this.setState({ [event.target.name]: event.target.value }, () =>
      console.log(this.state)
    );
  };

  render() {
    return (
      <div>
        <h1>Forma ideale</h1>
        <form onSubmit={this.submited}>
          <div>
            <label>name</label>
            <input
              name="name"
              type="text"
              placeholder="Your name"
              value={this.state.name}
              onChange={this.changed}
            />
            <div style={{ color: "red", fontSize: 12 }}>
              {this.state.nameError}
            </div>
          </div>
          <div>
            <label>e-mail</label>
            <input
              // type="email" cant be submitted and therefore our error message dont appear!
              type="text"
              name="email"
              placeholder="Your email"
              value={this.state.email}
              onChange={this.changed}
            />
            <div style={{ color: "red", fontSize: 12 }}>
              {this.state.emailError}
            </div>
          </div>
          <div>
            <label>password</label>
            <input
              type="password"
              name="password"
              placeholder="Your password"
              value={this.state.password}
              onChange={this.changed}
            />
            <div style={{ color: "red", fontSize: 12 }}>
              {this.state.passwordError}
            </div>
          </div>
          <div>
            <label>URL</label>
            <input
              // type="url" cant be submitted and therefore our error message dont appear!
              type="text"
              name="url"
              placeholder="https://example.com"
              value={this.state.url}
              onChange={this.changed}
            />
            <div style={{ color: "red", fontSize: 12 }}>
              {this.state.urlError}
            </div>
          </div>
          <button type="submit">Submit here !</button>
        </form>
        <p>{this.state.name}</p>
        <p>{this.state.email}</p>
        <p>{this.state.password}</p>
        <p>{this.state.url}</p>
      </div>
    );
  }
}

export default Form;
